<script setup>
</script>

<template>
    <h1>Grow animals</h1>
    <div class="form-container">
        <input v-model="formData.name" type="text" name="name" placeholder="Type animal name"/>
        <label for="form-select">Animal type</label>
        <select v-model="formData.type_id" name="categories" id="form-select">
            <option v-for="category in categories" key="index" :value="category.id">{{ category.type }}</option>
        </select>
        <button @click="createAnimal">Create new animal</button>
    </div>
    <div class="items-wrapper">
        <div class="item-container" v-for="item in items" key="index">
            <div @click="makeOlder(item.name)" :style="`width: ${width * item.size}px; height: ${height * item.size}px`"
                 class="icon-container" v-html="iconMap[item.animal_type.type]"></div>
            <span>Name: {{ item.name }}</span>
            <span>Age: {{ item.age }}</span>
            <span>Size: {{ item.size }}</span>
            <span>Type: {{ item.animal_type.type }}</span>
            <button @click="deleteAnimal(item.name)">Delete animal</button>
        </div>
    </div>
</template>

