    mounted() {
        this.interval = setInterval(() => {
            this.fetchData()
        }, this.iterationInterval)
    },
    beforeMount() {
        this.getCategories()
        this.fetchData()
    },
    beforeUnmount() {
        clearInterval(this.interval)
    },
    methods: {
        fetchData() {
            axios
                .get(`${this.api_url}/api/animal`)
                .then(response => (this.items = response.data.data))
                .catch(error => console.log(error));
        },
        makeOlder(name) {
            axios
                .post(`${this.api_url}/api/animal/make_older`, {
                    name: name
                })
                .then(function (response) {
                        console.log(response)
                        this.fetchData()
                    }
                )
                .catch(error => console.log(error));
        },
        getCategories() {
            axios
                .get(`${this.api_url}/api/animal_types`)
                .then(response => (this.categories = response.data.data))
                .catch(error => console.log(error));
        },
        createAnimal() {
            axios
                .post(`${this.api_url}/api/animal`, this.formData)
                .then(function (response) {
                        console.log(response)
                        this.fetchData()
                    }
                )
                .catch(error => console.log(error));
        },
        deleteAnimal(name) {
            axios
                .post(`${this.api_url}/api/animal/delete`, {
                    name: name
                })
                .then(function (response) {
                        console.log(response)
                        this.fetchData()
                    }
                )
                .catch(error => console.log(error));
        }
    }
}
</script>

<style>
@import url('https://fonts.googleapis.com/css2?family=Roboto&display=swap');

body {
    background-color: #65b2ff;
}

#app {
    font-family: Roboto, Helvetica, Arial, sans-serif;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    text-align: center;
    color: #2c3e50;
    margin-top: 60px;
}

.icon-container {
    transition: all .25s linear;
    position: relative;
}

.icon-container::after {
    content: "Click to grow";
    color: white;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    opacity: 0;
    transition: opacity .25s ease;
}

.icon-container:hover {
    cursor: zoom-in;
}

.icon-container:hover::after {
    opacity: 1;
}

.icon-container svg {
    width: 100%;
    height: 100%;
}

.form-container, .item-container, .items-wrapper {
    display: flex;
    flex-direction: column;
    gap: 15px;
    align-items: center;
}

.form-container {
    flex-direction: row;
    justify-content: center;
    margin-bottom: 30px;
}

.items-wrapper {
    gap: 32px;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: center;
}
</style>
